@import url("https://unpkg.com/modern-normalize@1.1.0/modern-normalize.css");

header {
  background: var(--color-accent);
  color: var(--background);
  --max-width: none;
}

.section-container {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  place-items: center flex-start; 
  padding-top: 1.5rem;
  padding-bottom: 4rem;
}

nav {
  display: flex;
  align-items: center;
  gap: 1.5rem;

  a.link {
    display: none;
    border-bottom: 2px solid transparent;
    transition: border-color 0.1s;

    &:hover {
      border-color: var(--color-accent, #154bf4);
    }
  }

  button#close {
    display: none;
  }
}

.logo {
  font-size: 2rem;
  font-weight: 600;
  line-height: 1.4;
  text-align: center;  
  justify-self: center;
}

.call-to-action {
  display: flex;
  align-items: center;
  gap: 1.5rem;
  margin-left: auto;

  button#open {
    color: var(--color-background); 
    margin-left: auto;
  }

  a {
    display: none;
    border-bottom: 2px solid transparent;
    transition: border-color 0.1s;

    &:hover {
      border-color: var(--color-accent, #154bf4);
    }
  }
}

nav#mobile-nav {
  position: absolute;
  flex-direction: column;
  text-align: center;
  background-color: var(--background-color, white);
  box-shadow: var(--box-shadow);
  border-radius: var(--border-radius);
  left: 0.5rem;
  top: 0.5rem;
  right: 0.5rem;
  padding: 2rem;

  a.logo {
    margin-right: 0;
  }

  a.link {
    display: block;
  }

  hr {
    width: 100%;
  }

  .button {
    width: 100%;
    justify-content: center;
  }

  button#close {
    display: block;
    position: absolute;
    right: 1rem;
    top: 1rem;

    svg {
      color: var(--color-accent, #154bf4);
      width: 1.5rem;
      height: 1.5rem;
    }
  }
}

@media (min-width: 600px) {
  #open,
  #mobile-nav {
    display: none;
  }

  nav a.link {
    display: inline-block;
  }

  .call-to-action a {
    display: inline-block;
  }
}

